    <link rel="stylesheet" type="text/css" href="./assets/css">
    <link rel="stylesheet" href="./assets/font-awesome.min.css">
    <link href="./assets/material-dashboard.css" rel="stylesheet">
    <link href="./assets/demo.css" rel="stylesheet">
	<script src="./assets/jquery.min.js"></script>
	<script defer="" src="./assets/all.js"></script>

    <title>CraftRise Login Checker</title>

</head>

<body style="width: 100%;">

    <center>
        <div class="col-md-8" style="width: 100%;">
            <div class="card" style="width: 100%;">
                <div class="card-header bg-dark mb-3">
                    <h3 class="card-title" style="color: white;"> GATE CRAFTRISE </h3>
                    <p class="card-category"> [ Sadece Bunlar Geçerlidir: Email:Pass or Username:Pass ]</p>
                </div>
                <div class="card-body">
				<br>
                    <textarea cols="120px" rows="12px" id="list" placeholder="sucuahmet42|ahmetabinaptin" style="text-align: center; margin: 0px; width: 810px; height: 220px; -webkit-box-sizing: border-box; -moz-box-sizing: border-box; box-sizing: border-box; width: 100%;"></textarea>
                    <p></p>
                    <center>
						[ Durum: <span class="label label-info" id="status">İşlem Bekleniyor</span> ]<br>  
						[ Toplam: <span class="label label-info" id="carregadas">0</span>  
						Başarılılar: <span class="label label-info" id="cLive">0</span> 
						Başarısızlar: <span class="label label-info" id="cDie">0</span> 
						Test Edilenler: <span class="class=" label="" label-info"="" id="total">0</span> ]
                        <p></p>
                        <div class="col-md-12">
                            <button type="submit" id="start" class="btn btn-success"> Başlat </button>
                            <button type="submit" id="stop" class="btn btn-danger"> Durdur </button>
                        </div>
                </center></div>
            </div>
        </div>
        </center>

        <center>
            <div class="col-md-8">
                <div class="card">
                    <h5 class="card-category"> [ Başarılılar ] </h5>
                    <div id="lives"></div>
                    <br>
                </div>

                <center>
                    <div class="col-md-15">
                        <div class="card">
                            <h5 class="card-category"> [ Başarısızlar ] </h5>
                            <div id="dies"></div>
                            <br>
                        </div>
	<script>
   // var audio = new Audio('blop.mp3'); -> isterseniz kullanın
    $(document).ready(function() {
        $('#start').attr('disabled', null);
        $('#start').click(function() {
            //audio.play();

            var line = $('#list').val().split('\n');
            var total = line.length;
            var ap = 0;
            var rp = 0;
            var st = 'Komut Bekleniyor!';
            $('#carregadas').html(total);
            $('#status').html(st);
            line.forEach(function(value) {
                var list = value.split('|');
                var ajaxCall = $.ajax({
                    url: 'api.php',
                    type: 'GET',
					data: {istek: value},
                    beforeSend: function() {
                        $('#stop').attr('disabled', null);
                        $('#start').attr('disabled', 'disabled');
                        var st = 'Başlatıldı!';
                        $('#status').html(st);

                    },

                    success: function(data) {
                        if (data.indexOf("Giriş Başarılı") >= 0) {
                            $("#lives").val(data + "\n" + $("#lives").val());
                            ap = ap + 1;
                            document.getElementById("lives").innerHTML += data + "";

                            //audio.play(); --> Aktif hesap çıkınca
                            removelinha();
                        } else {
                            $("#dies").val(data + "\n" + $("#dies").val());

                            rp = rp + 1;
                            document.getElementById("dies").innerHTML += data + "";
                            removelinha();
                        }
                        var fila = parseInt(ap) + parseInt(rp);
                        $('#cLive').html(ap);
                        $('#cDie').html(rp);
                        $('#total').html(fila);
                        if (fila == total) {
                            $('#start').attr('disabled', null);
                            $('#stop').attr('disabled', 'disabled');
                            audio.play();
                            var st = 'Başarıyla Bitti';
                            $('#status').html(st);

                        }

                    }

                });
                $('#stop').click(function() {
                    ajaxCall.abort();
                    $('#start').attr('disabled', null);
                    $('#stop').attr('disabled', 'disabled');
                    var st = 'Durduruldu';
                    $('#status').html(st);
                });
            });
        });
    });

    function removelinha() {
        var lines = $("#list").val().split('\n');
        lines.splice(0, 1);
        $("#list").val(lines.join("\n"));
    }
                </script>
            </div>





        </center></div>



</center></body></html>

